import{_ as l,c as i,ag as e,o as t}from"./chunks/framework.q0coUhEw.js";const b=JSON.parse('{"title":"图形认知","description":"","frontmatter":{},"headers":[],"relativePath":"kids/basic/shapes/index.md","filePath":"kids/basic/shapes/index.md","lastUpdated":1736935429000}'),r={name:"kids/basic/shapes/index.md"};function o(h,a,s,d,n,c){return t(),i("div",null,a[0]||(a[0]=[e('<h1 id="图形认知" tabindex="-1">图形认知 <a class="header-anchor" href="#图形认知" aria-label="Permalink to &quot;图形认知&quot;">​</a></h1><h2 id="什么是图形" tabindex="-1">什么是图形？ <a class="header-anchor" href="#什么是图形" aria-label="Permalink to &quot;什么是图形？&quot;">​</a></h2><p>图形是我们在编程中经常使用的基本元素。在生活中,我们可以看到很多不同的图形,比如圆形的太阳、正方形的积木、三角形的屋顶等。</p><h2 id="基本图形介绍" tabindex="-1">基本图形介绍 <a class="header-anchor" href="#基本图形介绍" aria-label="Permalink to &quot;基本图形介绍&quot;">​</a></h2><h3 id="圆形-⭕" tabindex="-1">圆形 ⭕ <a class="header-anchor" href="#圆形-⭕" aria-label="Permalink to &quot;圆形 ⭕&quot;">​</a></h3><ul><li>圆形没有棱角</li><li>从任何方向看都是一样的</li><li>生活中的例子：车轮、硬币、饼干</li></ul><h3 id="正方形-⬛" tabindex="-1">正方形 ⬛ <a class="header-anchor" href="#正方形-⬛" aria-label="Permalink to &quot;正方形 ⬛&quot;">​</a></h3><ul><li>有四条一样长的边</li><li>有四个直角</li><li>生活中的例子：积木、魔方、相框</li></ul><h3 id="三角形-△" tabindex="-1">三角形 △ <a class="header-anchor" href="#三角形-△" aria-label="Permalink to &quot;三角形 △&quot;">​</a></h3><ul><li>有三条边和三个角</li><li>是最基本的多边形</li><li>生活中的例子：三角尺、帐篷、披萨</li></ul><h3 id="长方形-▭" tabindex="-1">长方形 ▭ <a class="header-anchor" href="#长方形-▭" aria-label="Permalink to &quot;长方形 ▭&quot;">​</a></h3><ul><li>有两条长边和两条短边</li><li>有四个直角</li><li>生活中的例子：书本、手机、电视屏幕</li></ul><h2 id="在编程中的应用" tabindex="-1">在编程中的应用 <a class="header-anchor" href="#在编程中的应用" aria-label="Permalink to &quot;在编程中的应用&quot;">​</a></h2><ol><li>我们可以用这些基本图形来创作有趣的画面</li><li>通过组合不同的图形可以制作动画和游戏</li><li>学习图形可以帮助我们更好地理解坐标和位置</li></ol><h2 id="有趣的小知识" tabindex="-1">有趣的小知识 <a class="header-anchor" href="#有趣的小知识" aria-label="Permalink to &quot;有趣的小知识&quot;">​</a></h2><ul><li>所有的复杂图形都可以由基本图形组成</li><li>在计算机中，我们可以通过改变图形的大小、颜色和位置来创造有趣的效果</li><li>图形编程是开启编程世界的一扇有趣的大门</li></ul>',16)]))}const _=l(r,[["render",o]]);export{b as __pageData,_ as default};
