import{_ as l,c as i,ag as e,o as r}from"./chunks/framework.q0coUhEw.js";const b=JSON.parse('{"title":"少儿编程课程体系","description":"","frontmatter":{},"headers":[],"relativePath":"kids/index.md","filePath":"kids/index.md","lastUpdated":1736935429000}'),t={name:"kids/index.md"};function h(o,a,n,d,u,s){return r(),i("div",null,a[0]||(a[0]=[e('<h1 id="少儿编程课程体系" tabindex="-1">少儿编程课程体系 <a class="header-anchor" href="#少儿编程课程体系" aria-label="Permalink to &quot;少儿编程课程体系&quot;">​</a></h1><h2 id="启蒙阶段-3-6岁" tabindex="-1">启蒙阶段 (3-6岁) <a class="header-anchor" href="#启蒙阶段-3-6岁" aria-label="Permalink to &quot;启蒙阶段 (3-6岁)&quot;">​</a></h2><p>在这个阶段，我们通过趣味性的方式让孩子认识编程的基本概念：</p><h3 id="图形认知" tabindex="-1">图形认知 <a class="header-anchor" href="#图形认知" aria-label="Permalink to &quot;图形认知&quot;">​</a></h3><ul><li>认识基本图形</li><li>图形组合练习</li><li>趣味拼图游戏</li></ul><h3 id="颜色世界" tabindex="-1">颜色世界 <a class="header-anchor" href="#颜色世界" aria-label="Permalink to &quot;颜色世界&quot;">​</a></h3><ul><li>基本颜色认知</li><li>颜色混合实验</li><li>创意绘画练习</li></ul><h3 id="数字游戏" tabindex="-1">数字游戏 <a class="header-anchor" href="#数字游戏" aria-label="Permalink to &quot;数字游戏&quot;">​</a></h3><ul><li>数字认知与计数</li><li>简单数学运算</li><li>数字序列训练</li></ul><h3 id="简单动画" tabindex="-1">简单动画 <a class="header-anchor" href="#简单动画" aria-label="Permalink to &quot;简单动画&quot;">​</a></h3><ul><li>动画基础入门</li><li>角色动画制作</li><li>场景切换效果</li></ul><h3 id="趣味编程" tabindex="-1">趣味编程 <a class="header-anchor" href="#趣味编程" aria-label="Permalink to &quot;趣味编程&quot;">​</a></h3><ul><li>图形化编程启蒙</li><li>简单指令操作</li><li>有趣的小项目实践</li></ul><h2 id="基础阶段-7-9岁" tabindex="-1">基础阶段 (7-9岁) <a class="header-anchor" href="#基础阶段-7-9岁" aria-label="Permalink to &quot;基础阶段 (7-9岁)&quot;">​</a></h2><p>这个阶段开始培养孩子的逻辑思维能力：</p><h3 id="图形绘制" tabindex="-1">图形绘制 <a class="header-anchor" href="#图形绘制" aria-label="Permalink to &quot;图形绘制&quot;">​</a></h3><ul><li>掌握基础绘图命令</li><li>创意图案设计</li><li>动画效果制作</li></ul><h3 id="序列与循环" tabindex="-1">序列与循环 <a class="header-anchor" href="#序列与循环" aria-label="Permalink to &quot;序列与循环&quot;">​</a></h3><ul><li>理解程序流程</li><li>学习循环结构</li><li>掌握条件判断</li></ul><h3 id="互动故事创作" tabindex="-1">互动故事创作 <a class="header-anchor" href="#互动故事创作" aria-label="Permalink to &quot;互动故事创作&quot;">​</a></h3><ul><li>故事情节构思</li><li>场景设计制作</li><li>实现互动效果</li></ul><h2 id="进阶阶段-10-12岁" tabindex="-1">进阶阶段 (10-12岁) <a class="header-anchor" href="#进阶阶段-10-12岁" aria-label="Permalink to &quot;进阶阶段 (10-12岁)&quot;">​</a></h2><p>在这个阶段，孩子们将接触更专业的编程概念：</p><h3 id="项目开发" tabindex="-1">项目开发 <a class="header-anchor" href="#项目开发" aria-label="Permalink to &quot;项目开发&quot;">​</a></h3><ul><li>游戏设计与制作</li><li>动画项目创作</li><li>交互应用开发</li></ul><h3 id="算法思维" tabindex="-1">算法思维 <a class="header-anchor" href="#算法思维" aria-label="Permalink to &quot;算法思维&quot;">​</a></h3><ul><li>基础算法入门</li><li>问题解决能力</li><li>逻辑思维训练</li></ul><h3 id="网页设计基础" tabindex="-1">网页设计基础 <a class="header-anchor" href="#网页设计基础" aria-label="Permalink to &quot;网页设计基础&quot;">​</a></h3><ul><li>HTML基础知识</li><li>CSS样式入门</li><li>网页交互实现</li></ul><h2 id="我们的特色" tabindex="-1">我们的特色 <a class="header-anchor" href="#我们的特色" aria-label="Permalink to &quot;我们的特色&quot;">​</a></h2><ul><li>📚 循序渐进的学习体系</li><li>🎮 寓教于乐的教学方式</li><li>🎯 注重实践的课程设计</li><li>🌈 激发创造力的项目制作</li><li>🤝 个性化的教学指导</li></ul><h2 id="联系我们" tabindex="-1">联系我们 <a class="header-anchor" href="#联系我们" aria-label="Permalink to &quot;联系我们&quot;">​</a></h2><p>如果您对我们的少儿编程课程感兴趣，欢迎联系我们了解更多详情：</p><ul><li>📧 邮箱：contact@funfe.cn</li><li>🌐 网站：<a href="https://funfe.cn" target="_blank" rel="noreferrer">https://funfe.cn</a></li></ul>',34)]))}const q=l(t,[["render",h]]);export{b as __pageData,q as default};
